                        Nuevo.CG_CIA = selectedRecord.CG_CIA;
                        Nuevo.USUARIO = selectedRecord.USUARIO;

                        operarios.Add(Nuevo);
                        await Http.PostAsJsonAsync("api/Operario", Nuevo);

